import axios from "axios"
import { useState, useEffect } from "react"

const ClassTable = ({sem_id, c_id}) =>{
const [classes, setClasses] = useState([])
    useEffect( () => {
        const fetchClass = async () => {
                try {
                    const res = await axios.get(`http://localhost:8800/class/${sem_id}/${c_id}`)
                    setClasses(res.data);
                } catch (err){
                    console.log(err)
                }
        }
        fetchClass()
    },[])
    console.log(classes);
    return (
        <table className="table table-bordered">
            <thead>
                <tr>
                    <th className="col">Mã môn học</th>
                    <th className="col">Toà</th>
                    <th className="col">Phòng</th>
                    <th className="col">Mã giảng viên</th>
                    <th className="col">Tên giảng viên</th>
                    <th className="col">Hành động</th>
                </tr>
            </thead>
            <tbody>
                {classes.map(clas => (
                    <tr>
                        <th className>
                            {clas.group_id}
                        </th>
                        <td>
                            {clas.class_building}
                        </td>
                        <td>
                            {clas.room_number}
                        </td>
                        <td>
                            {clas.lecturer_id}
                        </td>
                        <td>
                            {clas.lecturer_name}
                        </td>
                        <td className=" text-center">
                            <button type="button" class="btn btn-primary">Xem lịch học</button>
                            <button type="button" class="btn btn-danger">Xoá lớp</button>
                        </td>
                    </tr> ))
                }        
            </tbody>
            Tổng số lớp : {classes.length}
        </table>
    )
}

export default ClassTable
