import axios from "axios"
import { useState, useEffect } from "react"

const ClassTable = ({semester_id, course_id}) =>{
const [classes, setClasses] = useState([])
    useEffect( () => {
        const fetchClass = async () => {
                try {
                    const res = await axios.get(`/class/${semester_id}/${course_id}`)
                    setClasses(res.data);
                } catch (err){
                    console.log(err)
                }
        }
        fetchClass()
    },)
    return (
        <table className="table table-bordered">
            <thead>
                <tr>
                    <th className="col">Mã môn học</th>
                    <th className="col">Toà</th>
                    <th className="col">Phòng</th>
                    <th className="col">Mã giảng viên</th>
                    <th className="col">Tên giảng viên</th>
                </tr>
            </thead>
            <tbody>
                {classes.map(student => (
                    <tr>
                        <th className>
                            {classes.group_id}
                        </th>
                        <td>
                            {classes.class_building}
                        </td>
                        <td>
                            {classes.room_number}
                        </td>
                        <td>
                            {classes.lecturer_id}
                        </td>
                        <td>
                            {classes.lecturer_name}
                        </td>
                    </tr> ))
                }        
            </tbody>
            Tổng số học sinh đăng kí : {students.length}
        </table>
    )
}

export default ClassTable
