import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import "./style.css"

const StudentRegister  = () =>{
    const location = useLocation();
    const s_id = location.pathname.split("/")[2];
    const semester_id = 231;
    const [student, setStudent] = useState({})
    const [courseRegister, setCourseRegister] = useState([])
    useEffect (() =>{
        const fetchStudent = async () => {
            try {
                const res = await axios.get(`http://localhost:8800/student/${s_id}`)
                setStudent(res.data);
            } catch (err){
                console.log(err)
            }
        }
        const fetchRegister = async () => {
            try {
                const res = await axios.get(`http://localhost:8800//register/${semester_id}/${s_id}`)
                setCourseRegister(res.data);
            } catch (err){
                console.log(err)
            }
        }
        fetchRegister()
    },[])

    console.log(student);
    console.log(courseRegister)
    return (
        <div>
            <h1 className="title">Đăng kí môn </h1>
            <div className="flexContainer">         
                <div className="boxInfo">
                    <h2>Thông tin sinh viên</h2>
                    <p>MSSV: {student.student_id}</p>
                    <p>Họ và tên: {student.student_name}</p>
                </div>
                <div className="boxInfo">
                    <h2>Môn học đã đăng kí trong học kì 231</h2>
                    <table className="table table-bordered">
                    <thead>
                        <tr>
                            <th className="col">Mã môn học</th>
                            <th className="col">Tên môn học</th>
                            <th className="col">Số tín chỉ</th>
                        </tr>
                    </thead>
                    <tbody>
                        {courseRegister.map(course => (
                            <tr key = {course.course_id}>
                                <th className>
                                    {course.course_id}
                                </th>
                                <td>
                                    {course.course_name}
                                </td>
                                <td>
                                    {course.credits}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}

export default StudentRegister;
