import React, { useEffect, useState } from "react";
import axios from 'axios'
import Table from 'react-bootstrap/Table';

const Students = () => {
    const [students, setStudents] = useState([])
    useEffect(() =>{
        const fetchAllStudents = async () => {
            try {
                const res = await axios.get("http://localhost:8800/student")
                setStudents(res.data);
            } catch (err){
                console.log(err)
            }
        }
        fetchAllStudents()
    },[])
    return (
        <div className="container text-center">
            <h1>Thông tin sinh viên</h1>
            <Table className="table">
            <thead>
        <tr>
          <th>#</th>
          <th>First Name</th>
          <th>Last Name</th>
          <th>Username</th>
        </tr>
      </thead>
              {/* <thead>
                <div className="col">Mã số sinh viên</div>
                <div className="col">Họ và tên</div>
                <div className="col">Lớp</div>
                <div className="col">Địa chỉ</div>
                <div className="col">Địa chỉ mail</div>
                <div className="col">Số điện thoại</div>
                <div className="col">Khoa</div>
                <div className="col">Cố vấn</div>
                <div className="col">Loại</div>
              <thead/> */}
            </Table>
            {students.map(student => (
                <table className="table">
                    <div className="col">
                        {student.student_id}
                    </div>
                    <div className="col">
                        {student.student_name}
                    </div>
                    <div className="col">
                        {student.student_class}
                    </div>
                    <div className="col">
                        {student.address}
                    </div>
                    <div className="col">
                        {student.mail_address}
                    </div>
                    <div className="col">
                        {student.phone_number}
                    </div>
                    <div className="col">
                        {student.major_id}
                    </div>
                    <div className="col">
                        {student.advisor_id}
                    </div>
                    <div className="col">
                        {student.student_type}
                    </div>
                </table>
            ))}
        </div>
    )
}

export default Students
