import { useState } from "react"
import { Link } from "react-router-dom";

const Register = () => {
    const [student, setStudent] = useState();
    const handleChange = (e) => {
        setStudent(e.target.value)
    }
    return (
        <div>
            <p>Đăng kí môn học. Nhập mã số sinh viên</p>
            <form action="">
                <div className="mx-sm-3 mb-2 ">
                <label  className="form-label">Mã số sinh viên</label>
                <input type="number" name='student_id' className="form-control" onChange={handleChange}/>
                </div>
                <button><Link to = {`/register/${student}`}>Confirm</Link></button>
            </form>

            <p>Thống kê số lượng đăng ký</p>
            <p>Danh sách học đăng kí theo môn học</p>
            <p>Tạo lớp cho môn học</p>
        </div>
    )
}

export default Register
