import React, { useEffect, useState } from "react";
import axios from 'axios'

const Students = () => {
    const [students, setStudents] = useState([])
    useEffect(() =>{
        const fetchAllStudents = async () => {
            try {
                const res = await axios.get("http://localhost:8800/student")
                setStudents(res.data);
            } catch (err){
                console.log(err)
            }
        }
        fetchAllStudents()
    },[])
    return (
        <div className="containerr">
            <h1>Thông tin sinh viên</h1>
            <table className="table">
              <thead>
                <th className="col">Mã số sinh viên</th>
                <th className="col">Họ và tên</th>
                <th className="col">Lớp</th>
                <th className="col">Địa chỉ</th>
                <th className="col">Địa chỉ mail</th>
                <th className="col">Số điện thoại</th>
                <th className="col">Khoa</th>
                <th className="col">Cố vấn</th>
                <th className="col">Loại</th>
              </thead>
            </table>
            {students.map(student => (
                <table className="table">
                    <div className="col">
                        {student.student_id}
                    </div>
                    <div className="col">
                        {student.student_name}
                    </div>
                    <div className="col">
                        {student.student_class}
                    </div>
                    <div className="col">
                        {student.address}
                    </div>
                    <div className="col">
                        {student.mail_address}
                    </div>
                    <div className="col">
                        {student.phone_number}
                    </div>
                    <div className="col">
                        {student.major_id}
                    </div>
                    <div className="col">
                        {student.advisor_id}
                    </div>
                    <div className="col">
                        {student.student_type}
                    </div>
                </table>
            ))}
        </div>
    )
}

export default Students
