import React, { useEffect, useState } from 'react'
import axios from 'axios';

const RegisterInfo = () => {
    const semester_id = 232;
    const [courses, setCourses] = useState([])
    const [studentRegister, setStudentRegister]= useState([[]])
    useEffect(() =>{
        const fetchAllCourses = async () => {
            try {
                const res = await axios.get("http://localhost:8800/course")
                setCourses(res.data);
            } catch (err){
                console.log(err)
            }
        }
        fetchAllCourses()
    },[])
    useEffect(() => {
        const fetchStudent =  async () =>  {courses.forEach(async (course) => {
                try {
                    // {...prev, [e.target.name]: (e.target.value).substring(0,6)}
                    const res = await axios.get(`http://localhost:8800/registerbycourse/${semester_id}/${course.course_id}`)
                    setStudentRegister(prev => [...prev, res.data]);
                } catch (err){
                    console.log(err)
                }
            })}
            fetchStudent()
    }, [])

    console.log(studentRegister)

    return (
        <div>
            <div className="title">
                <h1>Thống kê đăng kí theo môn học học kì {semester_id}</h1>
            </div>
            {
                courses.map((course, id) => {
                    <div className="flexContainer">         
                    <div className="boxInfo">
                        <h2>Môn học</h2>
                        <p>Mã môn học: {course.course_id}</p>
                        <p>Tên môn học: {course.course_name}</p>
                    </div>
                    <div className="boxInfo">
                        <h2>Danh sách sinh viên đăng kí môn học</h2>
                            <table className="table table-bordered">
                            <thead>
                                <tr>
                                    <th className="col">MSSV</th>
                                    <th className="col">Họ và tên</th>
                                </tr>
                            </thead>
                            <tbody>

                                    <tr>
                                        <th className>
                                            2153252
                                        </th>
                                        <td>
                                            Nguyen Minh Duy
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className>
                                            2153252
                                        </th>
                                        <td>
                                            Nguyen Minh Duy
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className>
                                            2153252
                                        </th>
                                        <td>
                                            Nguyen Minh Duy
                                        </td>
                                    </tr>

                            </tbody>
                            Tổng số học sinh đăng kí : 
                            </table>
                    </div>
                </div>
                })    
            }
        </div>
    )
}

export default RegisterInfo
