import { useLocation } from "react-router-dom"
import { useState, useEffect } from "react"
import axios from "axios"
import TimeTable from "../comp/timeTable"

const Time = () => {
    const location = useLocation();
    const local = location.pathname.split("/")
    const semester_id = local[2]
    const course_id = local[3]
    const group_id = local[4]
    const [classes, setClasses] = useState([])
    useEffect( () => {
        const fetchClass = async () => {
            try {
                const res = await axios.get(`http://localhost:8800/class/${semester_id}/${course_id}`)
                setClasses(res.data);
            } catch (err){
                console.log(err)
            }
        }
        fetchClass()
    },[])
    console.log(classes);
    const clas = classes.forEach(element => {
        if (element.group_id === group_id)
            return element
    });
    console.log(clas);

    return (
        <div>
            <div className="title">
                <h1>Thông tin lớp học {group_id} môn {course_id}</h1>
            </div>
            <div className="flexContainer">         
                <div className="boxInfo">
                    <h2>Thông tin lớp</h2>
                    <p>Mã môn học: {course_id}</p>
                    <p>Tên môn học</p>
                </div>
                <div className="boxInfo">
                    <h2>Lịch học</h2>                
                    <TimeTable semester_id={semester_id} course_id={course_id} group_id={group_id} />
                </div>
            </div>

        </div>
    )
}

export default Time
